/* eslint-disable max-lines-per-function */
import React, { useState } from "react"
import Tile from "./Tile"

const Chessboard = () => {
  const initialBoard = [
    ["bR", "bKn", "bB", "bQ", "bK", "bB", "bKn", "bR"],
    ["bP", "bP", "bP", "bP", "bP", "bP", "bP", "bP"],
    [null, null, null, null, null, null, null, null],
    [null, null, null, null, null, null, null, null],
    [null, null, null, null, null, null, null, null],
    [null, null, null, null, null, null, null, null],
    ["wP", "wP", "wP", "wP", "wP", "wP", "wP", "wP"],
    ["wR", "wKn", "wB", "wQ", "wK", "wB", "wKn", "wR"]
  ]
  const [board, setBoard] = useState(initialBoard)
  const [selectedPiece, setSelectedPiece] = useState(null)

function handleMove(start, destination) {
  const newBoard = [...board]
  const { row: startRow, col: startCol } = start
  const { row: destRow, col: destCol } = destination
  const isDestinationEmpty = newBoard[destRow][destCol] === null
  const isEnemyPiece = newBoard[destRow][destCol] && newBoard[destRow][destCol][0] !== selectedPiece[0]

  if ((isDestinationEmpty || isEnemyPiece) && isValidMove(start, destination)) {
    newBoard[destRow][destCol] = selectedPiece
    newBoard[startRow][startCol] = null
    setBoard(newBoard)
    setSelectedPiece(null)
  } else {
    setSelectedPiece(null)
  }
}

// eslint-disable-next-line complexity
function isValidMove(start, destination, piece) {
  const { row: startRow, col: startCol } = start
  const { row: destRow, col: destCol } = destination

  if (destRow < 0 || destRow >= 8 || destCol < 0 || destCol >= 8) {
    return false
  }

  const rowDiff = Math.abs(destRow - startRow)
  const colDiff = Math.abs(destCol - startCol)

}
  const chessboard = board.map((row, rowIndex) => (
    <div key={rowIndex} className="board-row">
      {row.map((piece, colIndex) => (
        <Tile
          key={`${rowIndex}-${colIndex}`}
          piece={piece}
          onSquareClick={() => {
            if (piece && (!selectedPiece || piece[0] === selectedPiece[0])) {
              setSelectedPiece(piece)
            } else if (selectedPiece) {
              handleMove({ row: rowIndex, col: colIndex }, { row: rowIndex, col: colIndex })
            }
          }}
        />
      ))}
    </div>
  ))

   return <div className="chessboard">{chessboard}</div>
}

export default Chessboard
