import React from "react"

const Button = ({ isEven, label, rowIndex, colIndex, onMove, selectedPiece }) => {
  const [isClicked, setIsClicked] = useState(false)
  const handleClick = () => {
    setIsClicked(!isClicked)

    if (isClicked) {
      onMove({ label, rowIndex, colIndex })
      setIsClicked(false) 
    }
  }

  return (
    <button
      isEven={isEven}
      className={`tile ${isEven ? "bg-blue-200" : "bg-white"} flex items-center justify-center w-80 h-80 ${
        isClicked ? "clicked" : ""
      } ${selectedPiece && selectedPiece.rowIndex === rowIndex && selectedPiece.colIndex === colIndex ? "selected" : ""}`}
      onClick={handleClick}
      label={label}
    ></button>
  )
}


export default Button
