import React, { useState } from "react"
import Button from "./Button"

const Tile = ({ isEven, label, rowIndex, colIndex, onMove }) => {
  const [isClicked, setIsClicked] = useState(false)
const [startPosition, setStartPosition] = useState(null)
const handleClick = () => {
  setIsClicked(!isClicked)

  if (isClicked) {
    onMove({ start: startPosition, destination: { label, rowIndex, colIndex } })
    setStartPosition(null)
  } else {
    setStartPosition({ label, rowIndex, colIndex })
  }
}

  return (
    <Button
      isEven={isEven}
      className={`tile ${
        isEven ? "bg-blue-200" : "bg-white"
      } flex items-center justify-center ${isClicked ? "clicked" : ""}`}
      style={{ width: "80px", height: "80px" }}
      onClick={handleClick}
      label={label}
    ></Button>
  )
}
export default Tile
