import React, { useState } from "react"
import Button from "./Button"

const Tile = ({ isEven, label, onMove, rowIndex, colIndex }) => {
  const [isClicked, setIsClicked] = useState(false)
  const handleMove = () => {
    setIsClicked(!isClicked)
    onMove({ label, rowIndex, colIndex })
  }

  return (
    <Button
      isEven={isEven}
      className={`tile ${isEven ? "bg-red-200" : "bg-white"} flex items-center justify-center ${isClicked ? "clicked" : ""}`}
      style={{ width: "80px", height: "80px" }}
      onClick={handleMove}
      label={label}
    ></Button>
  )
}

export default Tile
